import React, { useEffect, useRef } from 'react';
import { Helmet } from 'react-helmet';

const BilldeskPayment = () => {
  const scriptRef = useRef(null);
  const configRef = useRef(null);

  const hideSpinner = () => {
    document.getElementById('spinner').style.display = 'none';
  };

  const showSpinner = () => {
    document.getElementById('spinner').style.display = 'block';
  };

  const responseHandler = async (txn) => {
    const responseXHR = await new XMLHttpRequest();
    responseXHR.onreadystatechange = function () {
      if (responseXHR.readyState === XMLHttpRequest.DONE) {
        if (responseXHR.status === 200) {
          const jsonStr = responseXHR.responseText;
          console.log(jsonStr);
          const jsonObj = JSON.parse(jsonStr);
          console.log(jsonObj);
          console.log(jsonObj.orderid);
          let htm = 'Successful Transaction';
          document.getElementById('result').innerHTML = htm;
          hideSpinner();
        } else if (responseXHR.status === 400) {
          // Handle error 400
        } else {
          // Handle other statuses
        }
      }
    };

    console.log(txn);
    if (txn.txnResponse) {
      responseXHR.open('POST', 'https://pguatweb.billdesk.io/pgtxnsimulator/v1_2/txnresponse', true);
      responseXHR.send(txn.txnResponse.transaction_response);
    } else {
      hideSpinner();
    }
  };

  const loadBillDeskSdk = () => {
    showSpinner();
    document.getElementById('result').innerHTML = '';
    window.loadBillDeskSdk(configRef.current);
  };


  // Initialize config outside of the useEffect
  const config = {
    responseHandler,
    merchantLogo: 'data:image/png;base64,',
    flowConfig: {
      merchantId: sessionStorage.getItem('merchantId'),
      bdOrderId: sessionStorage.getItem('bdOrderId'),
      authToken: sessionStorage.getItem('authToken'),
      childWindow: true,
    },
    flowType: 'payments',
  };

  // Set configRef.current to the initialized config
  configRef.current = config;

  useEffect(() => {
    // Load Bootstrap and jQuery scripts
    const bootstrapScript = document.createElement('script');
    bootstrapScript.src = 'https://stackpath.bootstrapcdn.com/bootstrap/3.4.1/js/bootstrap.min.js';
    bootstrapScript.integrity = 'sha384-aJ21OjlMXNL5UyIl/XNwTMqvzeRMZH2w8c5cRVpzpU8Y5bApTppSuUkhZXN0VxHd';
    bootstrapScript.crossOrigin = 'anonymous';
    bootstrapScript.defer = true;

    const jqueryScript = document.createElement('script');
    jqueryScript.src = 'https://ajax.googleapis.com/ajax/libs/jquery/3.4.1/jquery.min.js';
    jqueryScript.defer = true;

    // Load BillDesk SDK script
    const billdeskSdkScript = document.createElement('script');
    billdeskSdkScript.src = 'https://uat1.billdesk.com/merchant-uat/sdk/dist/billdesksdk/billdesksdk.esm.js';
    billdeskSdkScript.type = 'module';
    billdeskSdkScript.async = true;

    // Set a ref to the script elements
    scriptRef.current = [bootstrapScript, jqueryScript, billdeskSdkScript];

    // Append scripts to the head of the document
    document.head.appendChild(bootstrapScript);
    document.head.appendChild(jqueryScript);
    document.head.appendChild(billdeskSdkScript);

    // Set onload events once the scripts are added to the DOM
    billdeskSdkScript.onload = () => {
      showSpinner();
      document.getElementById('result').innerHTML = '';
      window.loadBillDeskSdk(configRef.current);
    };

    return () => {
      hideSpinner(); // Cleanup function
    };
  }, [configRef.current]);


  return (
    <div className="container mt-8">
      <Helmet>
        {/* Load required styles here */}
        <link
          href="https://uat1.billdesk.com/merchant-uat/sdk/dist/billdesksdk/billdesksdk.css"
          rel="stylesheet"
        />
      </Helmet>

      <div className="jumbotron w-80 mx-auto">
        <h1 className='text-4xl'>BillDesk Payment Gateway</h1>
        <a href="#" onClick={loadBillDeskSdk} className="btn btn-lg text-lg border p-2 mt-4" role="button">
          Pay Now »
        </a>
      </div>
      <div id="spinner" style={{ display: 'none' }} className="mt-3 text-center">
        <div className="spinner-border" role="status" style={{ width: '5rem', height: '5rem' }}>
          <span className="sr-only">Loading...</span>
        </div>
      </div>
      <div id="result" className="  text-green-500 text-xl font-semibold jumbotron mt-3">

      </div>
    </div>
  );
};


export default BilldeskPayment;
